mapper.select:
select c.task_info_value as public_code, a.agreement_id,a.order_id,a.project,a.num,a.cut_lot,a.conclusion_state, a.conclusion_message,a.invalidate_text,a.agreement_state,d.user_name, DATE_FORMAT(a.create_time,'%Y-%m-%d') as create_time,a.agreement_name, a.track_id, g.user_name as track_name, a.is_close from agreement_manage a left join task_order b on a.order_id = b.ORDER_ID left join task_order_info c on b.ORDER_ID = c.ORDER_ID and c.TASK_TYPE_INFO_ID = 1 left join sys_users d on a.create_user = d.USER_ID left join sys_users g on a.track_id = g.user_id
1 = 1 and c.TASK_INFO_VALUE like CONCAT('%',#{publishCode,jdbcType=VARCHAR},'%') and a.project like CONCAT('%',#{project,jdbcType=VARCHAR},'%') and a.cut_lot like CONCAT('%',#{cutLOT,jdbcType=VARCHAR},'%') and d.user_name like CONCAT('%',#{createUser,jdbcType=VARCHAR},'%') and DATE_FORMAT(a.create_time,'%Y-%m-%d') = #{createTime,jdbcType=VARCHAR} and a.agreement_state = #{agreementState,jdbcType=VARCHAR}
order by a.order_id desc

mapper.update:
update agreement_manage
is_close = #{isClose,jdbcType=NUMERIC}, track_id = #{trackId,jdbcType=NUMERIC}, num = #{num,jdbcType=NUMERIC}, project = #{project,jdbcType=VARCHAR}, cut_lot = #{cutLOT,jdbcType=VARCHAR}, agreement_state = #{agreementState,jdbcType=VARCHAR}, conclusion_state = #{conclusionState,jdbcType=VARCHAR}, conclusion_message = #{conclusionMessage,jdbcType=VARCHAR}, invalidate_text = #{invalidateText,jdbcType=VARCHAR},
where agreement_id = #{agreementId, jdbcType=NUMERIC}

mapper.select:
select count(1)
	from agreement_manage a
	where a.order_id = #{orderId,jdbcType=NUMERIC}
