mapper.select:
SELECT a.USER_MENU_ID as user_menu_id, a.MENU_ID as menu_id, a.USER_ID as user_id, b.MENU_NAME as menu_name, b.menu_url FROM sys_user_menu a, sys_menu b
a.MENU_ID = b.MENU_ID and b.systerm_flag = '1' AND b.DELETE_STATE = 0 and a.DELETE_STATE = 0 AND a.USER_ID = #{userId,jdbcType=NUMERIC} and b.module_id = #{moduleId,jdbcType=NUMERIC}
UNION
SELECT a.USER_MENU_ID as user_menu_id, a.MENU_ID as menu_id, a.USER_ID as user_id, b.MENU_NAME as menu_name, b.menu_url FROM sys_user_menu a, sys_menu b, dpcoi_user du
a.MENU_ID = b.MENU_ID and b.systerm_flag = '2' AND b.DELETE_STATE = 0 and a.DELETE_STATE = 0 and du.sys_user_id = a.user_id and du.del_flag = '0' and du.dpcoi_user_state = '1' AND a.USER_ID = #{userId,jdbcType=NUMERIC} and b.module_id = #{moduleId,jdbcType=NUMERIC}
