mapper.select:
		
		select s.password
  			from sys_users s
  			where s.user_code = #{userCode,jdbcType=VARCHAR}

mapper.update:
update sys_users
USER_WORK_ID = #{userWorkId,jdbcType=VARCHAR}, USER_NAME = #{userName,jdbcType=VARCHAR}, USER_SEX = #{userSex,jdbcType=VARCHAR}, USER_CODE = #{userCode,jdbcType=VARCHAR}, PASSWORD = #{password,jdbcType=VARCHAR}, ORG_ID = #{orgId,jdbcType=VARCHAR}, EMAIL = #{email,jdbcType=VARCHAR}, MOBILE_TEL = #{mobileTel,jdbcType=VARCHAR}, USER_MARK = #{userMark,jdbcType=VARCHAR}, DELETE_STATE = #{deleteState,jdbcType=VARCHAR}, ISHEADER = #{isHeader,jdbcType=VARCHAR}, rr_problem_persionliable = #{rrProblemPersionliable,jdbcType=VARCHAR}, rr_minister = #{rrMinister,jdbcType=VARCHAR},
where user_id=#{userId, jdbcType=NUMERIC}
