mapper.select:
select dor.`DPCOI_ORDER_ID` as dpcoiOrderId, dor.`ISSUED_NO` as issuedNo, dor.`DESIGN_CHANGE_NO` as designChangeNo, dor.`VEHICLE_NAME` as vehicleName, dor.`PRODUCT_NO` as productNo, dor.`HOPE_CUTTING_DATE` as hopeCuttingDate, dor.`REAL_CUTTING_DATE` as realCuttingDate, DATE_FORMAT(dor.`REAL_CUTTING_DATE`, '%Y-%m-%d') AS realCuttingDateStr, dor.`CHANGE_CONTENT` as changeContent, dor.`RELEASE_DATE` as releaseDate, dor.`RETURN_DATE` as returnDate, dor.`product_line` as productLine, DATE_FORMAT(dor.`RELEASE_DATE`, '%Y-%m-%d') AS releaseDateStr, DATE_FORMAT(dor.`RETURN_DATE`, '%Y-%m-%d') AS returnDateStr, dor.`DESIGN_ACT` as designAct, dor.`QUASI_ACT` as quasiAct, (SELECT users.`user_name` FROM sys_users users WHERE users.`USER_ID` = dor.`QUASI_ACT`) AS quasiActName, dor.`DPCOI_ORDER_STATE` as dpcoiOrderState, dor.`DPCOI_ORDER_TYPE` as dpcoiOrderType, dor.`TASK_ORDER_ID` AS taskOrderId, (SELECT info.`task_info_value` FROM task_order_info info WHERE info.`TASK_TYPE_INFO_ID` = '1' AND info.`ORDER_ID` = dor.`TASK_ORDER_ID`) AS taskOrderNo, dor.`REMARK` as remark, dor.`CREATE_BY` AS createBy, dor.rr_problem_id as rrProblemId, (select dwo.`DPCOI_WO_ORDER_TYPE` from dpcoi_wo_order dwo where dwo.`DPCOI_ORDER_ID` = dor.`DPCOI_ORDER_ID` and dwo.`DPCOI_WO_ORDER_STATE` in (1, 2, 3)) as dpcoiWoOrderType from dpcoi_order dor
dor.del_flag = 0 and dor.`DPCOI_ORDER_TYPE` in (1, 2)
and dor.`VEHICLE_NAME` like CONCAT('%',#{vehicleName},'%')
and dor.`PRODUCT_NO` like CONCAT('%',#{productNo},'%')
and dor.`HOPE_CUTTING_DATE` = #{hopeCuttingDate}
and DATE_FORMAT(dor.`REAL_CUTTING_DATE`,'%Y-%m-%d') >= DATE_FORMAT(#{realCuttingDateStart,jdbcType=DATE},'%Y-%m-%d')
and DATE_FORMAT(dor.`REAL_CUTTING_DATE`,'%Y-%m-%d') <= DATE_FORMAT(#{realCuttingDateEnd,jdbcType=DATE},'%Y-%m-%d')
and DATE_FORMAT(dor.`RELEASE_DATE`,'%Y-%m-%d') >= DATE_FORMAT(#{releaseDateStart,jdbcType=DATE},'%Y-%m-%d')
and DATE_FORMAT(dor.`RELEASE_DATE`,'%Y-%m-%d') <= DATE_FORMAT(#{releaseDateEnd,jdbcType=DATE},'%Y-%m-%d')
and dor.`pfmea_delay` = #{pfmeaDelay}
and dor.`cp_delay` = #{cpDelay}
and dor.`standard_book_delay` = #{standardBookDealy}
and EXISTS (SELECT 1 FROM task_order_info info WHERE info.`TASK_TYPE_INFO_ID` = '1' AND info.`ORDER_ID` = dor.`TASK_ORDER_ID` AND info.`task_info_value` like CONCAT('%',#{taskOrderNo},'%'))
AND dor.`DESIGN_CHANGE_NO` like CONCAT('%',#{designChangeNo},'%')
AND dor.`product_line` like CONCAT('%',#{productLine},'%')
order by dor.`DPCOI_ORDER_ID` desc limit #{start},#{size}

select dor.`DPCOI_ORDER_ID`
from dpcoi_order dor
where rr_problem_id = #{rrProblemId}

select count(1)
from dpcoi_order dor

    (dor.`ISSUED_NO` = #{issuedNo,jdbcType=VARCHAR}
    OR dor.`DESIGN_CHANGE_NO` = #{designChangeNo,jdbcType=VARCHAR})
    
        and dor.`DPCOI_ORDER_ID` <> #{dpcoiOrderId,jdbcType=BIGINT}
