mapper.select:
SELECT COUNT(1)
FROM dpcoi_rr_delay_statistics statis

    
        and DATE_FORMAT(statis.`delay_date`, '%Y-%m-%d') >= #{dateBegin}
    
    
        and DATE_FORMAT(statis.`delay_date`, '%Y-%m-%d') <= #{dateEnd}

SELECT statis.`persion_liable` AS userName,
COUNT(1) AS num
FROM dpcoi_rr_delay_statistics statis

    
        and DATE_FORMAT(statis.`delay_date`, '%Y-%m-%d') >= #{dateBegin}
    
    
        and DATE_FORMAT(statis.`delay_date`, '%Y-%m-%d') <= #{dateEnd}
    

GROUP BY statis.`persion_liable`

select statis.`problem_progress` as problemProgress, statis.`speed_of_progress` as speedOfPorgress, statis.`problem_status` as problemStatus, statis.tracking_level as trackingLevel, DATE_FORMAT(statis.`delay_date`, '%Y-%m-%d') as delayDate, rr.`problem_no` as problemNo FROM dpcoi_rr_delay_statistics statis, dpcoi_rr_problem rr
statis.`rr_problem_id` = rr.`id`
and DATE_FORMAT(statis.`delay_date`, '%Y-%m-%d') >= #{dateBegin}
and DATE_FORMAT(statis.`delay_date`, '%Y-%m-%d') <= #{dateEnd}
and statis.`persion_liable`= #{userName}
