mapper.update:
update task_wo_order
wo_order_state_code = #{woOrderStateCode,jdbcType=VARCHAR}, create_time = sysdate(), reply_user = #{replyUser,jdbcType=NUMERIC}, reply_time = sysdate(), complete_time = sysdate(), is_delay = #{isDelay,jdbcType=VARCHAR}, require_complete_time = #{requireCompleteTimeStr,jdbcType=VARCHAR}, wo_refuse_reason = #{woRefuseReason,jdbcType=VARCHAR},
where wo_order_id=#{woOrderId, jdbcType=NUMERIC}

mapper.select:
SELECT GROUP_CONCAT(users.`EMAIL`)
FROM task_wo_order two, task_tache_manager manager, sys_users users
 two.`TACHE_ID` = manager.`tache_id`
	AND manager.`user_id` = users.`USER_ID`
	AND users.`DELETE_STATE` = '0'
	 and two.WO_ORDER_STATE_CODE = #{woOrderStateCode,jdbcType=VARCHAR} 
	 and two.order_id = #{orderId,jdbcType=NUMERIC} 
	 and two.wo_order_id = #{woOrderId,jdbcType=NUMERIC}
